mapper.select:
SELECT c.id, c.nickname, c.email, c.website, c.logo, c.ip, c.content, c.qq, c.page, c.article_id AS articleId, a.title AS articleTitle, c.status, c.email_notify AS emailNotify, c.create_time AS createTime, c.update_time AS updateTime FROM comment c LEFT JOIN article a ON c.article_id = a.id
AND c.article_id = #{param1}
AND c.page = #{param2}
AND c.status = #{param3}
AND c.nickname LIKE CONCAT('%', #{param4}, '%')
ORDER BY c.create_time DESC

mapper.update:
UPDATE comment
nickname = #{nickname}, email = #{email}, website = #{website}, logo = #{logo}, ip = #{ip}, content = #{content}, qq = #{qq}, page = #{page}, article_id = #{articleId}, status = #{status}, email_notify = #{emailNotify}, update_time = NOW()
WHERE id = #{id}
