mapper.update:
UPDATE comment
nickname = #{nickname}, email = #{email}, website = #{website}, avatar = #{avatar}, ip = #{ip}, content = #{content}, page = #{page}, status = #{status}, notice = #{notice}, parentCommentId = #{parentCommentId}, blog_id = #{blogId}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT c.*, a.title
FROM comment c
LEFT JOIN article a ON c.blog_id = a.id


        AND c.blog_id = #{blogId}


        AND c.page = #{page}


        AND c.status = #{status}


        AND c.nickname LIKE CONCAT('%', #{nickname}, '%')


ORDER BY c.create_time DESC

SELECT c.*, a.title FROM comment c LEFT JOIN article a ON c.blog_id = a.id
AND c.blog_id = #{blogId}
AND c.page = #{page}
AND c.status = true
ORDER BY c.create_time ASC

select c.*, a.title
from comment as c left join article as a on c.blog_id=a.id
where c.parentCommentId=#{parentCommentId}
